import React, { Component } from 'react';

// Função para lidar com a submissão do formulário
function handleSubmit(e) {
    e.preventDefault();

    // Seleciona o formulário
    const form = e.target;

    // Prepara os dados do formulário para enviar
    const formData = new FormData(form);
    const url = 'https://script.google.com/macros/s/AKfycbw99F_zjQtTU21Jn8M-Hu32lEkhy4mB10BVGaxRK-FVHbf422sWOwz38WBfSf4Gbb_8bg/exec';

    // Faz a requisição POST para o endpoint do Google Sheets
    fetch(url, {
        method: 'POST',
        body: formData
    })
    .then(response => {
        console.log('Resposta do servidor:', response);
        if (!response.ok) {
            throw new Error('Erro ao enviar formulário:', response.statusText);
        }
        return response.json();
    })
    .then(data => {
        console.log('Resposta do servidor JSON:', data);
        // Aqui você pode lidar com a resposta do servidor, se necessário
    })
    .catch(error => {
        console.error('Erro ao enviar formulário:', error);
        // Aqui você pode lidar com erros de requisição
    });

    // Limpa os campos do formulário após o envio (opcional)
    form.reset();
}

class AppointmentForm extends Component {

    render() {
        return (
            <>
                <div className="appointment-form form-wraper">
                    <h3 className="title">Solicitar Agendamento</h3>
                    <div className="form-group">
                        <select className="form-select form-control">
                            <option defaultValue>Agendamento</option>
                        </select>
                    </div>
                    <div className="form-group">
                        <select className="form-select form-control">
                            <option defaultValue>Psicóloga Edineia</option>
                        </select>
                    </div>
                    <form action="#" className="form" onSubmit={handleSubmit}>
                        <div className="form-group">
                            <input type="text" className="form-control" name="Name" placeholder="Seu nome" />
                            <input type="number" className="form-control" name="Phone" placeholder="Número de telefone" />
                            <button type="submit" className="btn btn-secondary btn-lg">Solicitar Agendamento</button>
                        </div>
                    </form>
                </div>
            </>
        );
    }
}

export default AppointmentForm;
