import React, { Component } from 'react';
import axios from 'axios';

class AppointmentForm extends Component {
  handleSubmit = async (e) => {
    e.preventDefault();

    const formData = {
      type: e.target[0].value, // Adicionar a lógica para capturar os valores dos campos corretamente
      psychologist: e.target[1].value,
      name: e.target.Name.value,
      phone: e.target.Phone.value,
    };

    try {
      const response = await axios.post('https://script.google.com/macros/s/AKfycbw99F_zjQtTU21Jn8M-Hu32lEkhy4mB10BVGaxRK-FVHbf422sWOwz38WBfSf4Gbb_8bg/exec', formData);
      console.log('Formulário enviado com sucesso:', response);
      alert('Formulário enviado com sucesso!');
      // Limpar campos após o envio
      e.target.reset();
    } catch (error) {
      console.error('Erro ao enviar formulário:', error);
      alert('Erro ao enviar formulário. Por favor, tente novamente mais tarde.');
    }
  };

  render() {
    return (
      <>
        <div className="appointment-form form-wraper">
          <h3 className="title">Solicitar Agendamento</h3>
          <form action="#" className='form' onSubmit={this.handleSubmit}>
            <div className="form-group">
              <select className="form-select form-control">
                <option defaultValue>Agendamento</option>
                {/* Opções do select */}
              </select>
            </div>
            <div className="form-group">
              <select className="form-select form-control">
                <option defaultValue>Psicóloga Edineia</option>
                {/* Opções do select */}
              </select>
            </div>
            <div className="form-group">
              <input type="text" className="form-control" name="Name" placeholder="Seu nome" />
            </div>
            <div className="form-group">
              <input type="number" className="form-control" name="Phone" placeholder="Número de telefone" />
            </div>
            <button type="submit" className="btn btn-secondary btn-lg">Solicitar Agendamento</button>
          </form>
        </div>
      </>
    );
  }
}

export default AppointmentForm;
