import React, { Component } from 'react';

class AppointmentForm extends Component {

    // Função para lidar com o envio do formulário
    handleSubmit = (e) => {
        e.preventDefault();
        console.log("Formulário enviado");

        // Aqui você pode adicionar o código para enviar os dados para o seu endpoint desejado
        // Exemplo usando fetch:
        // fetch('https://seu-endpoint-de-envio', {
        //     method: 'POST',
        //     body: new FormData(e.target)
        // })
        // .then(response => response.json())
        // .then(data => console.log(data))
        // .catch(error => console.error('Erro ao enviar formulário:', error));
    }

    render() {
        return (
            <>
                <div className="appointment-form form-wraper">
                    <h3 className="title">Solicitar Agendamento</h3>
                    <div className="form-group">
                        <select className="form-select form-control">
                            <option defaultValue>Agendamento</option>
                        </select>
                    </div>
                    <div className="form-group">
                        <select className="form-select form-control">
                            <option defaultValue>Psicóloga Edineia</option>
                        </select>
                    </div>
                    <form action="#" className='form' onSubmit={this.handleSubmit}>
                        <div className="form-group">
                            <input type="text" className="form-control" name="Name" placeholder="Seu nome" />
                            <input type="number" className="form-control" name="Phone" placeholder="Número de telefone" />
                            <input type="submit" className="btn btn-secondary btn-lg" value="Enviar" />
                        </div>
                    </form>
                </div>
            </>
        );
    }
}

export default AppointmentForm;
